import React from 'react';
// import { Row, Col, Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import "./style2.css"
import GoogleLoginButton from "./GoogleLoginButton";


// presentational component, only a stateless function
// gets props by destructuring the props object
// note that the input fields use the props to render their value attribute
const LoginForm = ({
    username, password, 
    handleChangeUsername,  
    handleChangePassword, 
    handleSubmit,
    handleGoogleSignIn,
    validate,
    isAuthenticated
  }) => {
    return (

      <div className="wrapper fadeInDown">
        <div className="formContent">
          {/* <Row className="register-sns">
            <Col xs={6}> <input type="button" id="login-facebook" className="fadeIn register form-control" value="Login with Facebook" /></Col>
            <Col xs={6}> <input type="button" id="login-google" className="fadeIn register form-control" value="Login with Google" onSignIn={handleGoogleSignIn}
                isAuthenticated={isAuthenticated}/></Col>
          </Row>
        <Col xs={12} className="fadeIn">
          <img src="login4.png" id="usericon" alt="User Icon" />
        </Col> */}
          <form onSubmit={handleSubmit} method= "POST">
            <input type="text" id="username" className="fadeIn" name="id" placeholder="ID" autoFocus value = {username} onChange={handleChangeUsername}/>
            <input type="password" id="password" className="fadeIn" name="password" placeholder="Password" autoFocus value = {password} onChange={handleChangePassword}/>
            <input type="submit" id="submit" className="fadeIn" value="로그인" disabled={!validate(username, password)}/>

            <div className="Login-google">
              <GoogleLoginButton
                onSignIn={handleGoogleSignIn}
                isAuthenticated={isAuthenticated}
              />
            </div>


          </form>
        </div>

      </div>
    );
  }

export default LoginForm;
