import React, { Component } from 'react';

export default class MailAuth extends Component{
    constructor(props){
        super(props);

        this.state = {
            email : ""
        }
    }

    async resendAuthEmail(){
        let data={
            email: this.state.email
        };

        let handleErrors = response =>{
            if(response.message){
                throw Error(response.message);
            }
            return response;
        }

        fetch("http://localhost/api/send-auth-email", {
            method: "POST",
            body: JSON.stringify(data)
        })
        .then(handleErrors)
        .then(res=>res.json())
        .then(json=>{
            console.log(json);
            console.log("메일 재발송 완료.");
        });
    }

    render(){
        return(
            <div>
                <h2>
                    입력하신 이메일 주소 {this.state.email}을 통해 인증을 진행해주세요.
                </h2>
                <button onclick={resendAuthEmail}> </button>
            </div>
        );
    }
}
