import React, { Component, Fragment } from "react";
import LoginForm from "../components/LoginComponents/LoginForm";

//로그인
export default class Login extends Component { //export default : 다른 모듈에서 이 모듈을 import할 때 내보낼 대표 값
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: ""
    };
    console.log("로그인 시작.");
  }

  //유저 로그인 상태 체크
  componentDidMount() {
    if (this.props.isLogin) {
      this.props.history.push("/");
    }
  }

  validateField(username, password) {
    return (username&&username.length>0&&password&&password.length>0);
  }

  valChangeControl(e){
    let target_id=e.target.id;
    let target_val=e.target.value;
    console.log("changeControl!");
    this.setState({
      [target_id]: target_val
    });
    console.log("CC test.");
    console.log(this.state.username);
    console.log(this.state.password);
  }

  //구글 로그인 구현하기


  // 일반 로그인
  normalLogin(e) {
    e.preventDefault();

    let err = response => {
      console.log(response);
      if (response.hasOwnProperty("error")) { // response에 error 키를 가진 값이 있을 경우 에러 발생했다는 의미
        if(response.hasOwnProperty("email")){ //이메일 인증 안받았을 때
          this.props.userStateChange(true, false, data.username, response['email']); 
          localStorage.setItem('isLogin', true);
          this.props.history.push('/mail-resend'); //이메일 인증 페이지로 리다이렉트
        }
        else{ //아이디 비밀번호 틀렸을 때
           throw Error(response['error']);
        }
      }
      return response;
    }


    let data = { //let은 var를 대체하는 블락변수이다.
      username: this.state.username,
      password: this.state.password
    };

    // JWT 토큰을 통한 로그인 수행
    fetch('http://localhost/api/jwt-login', {
      //보통 fetch는 쿠키를 보내거나 받지 않는다. 쿠키를 전송하거나 받기 위해서는 credentials 옵션을 반드시 설정해야 한다.
      method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        credentials : 'include', //서버에 실을 때 수정
        body: JSON.stringify(data)
      })
    .then(res => res.json())
    .then(err)
    .then(content => {
      console.log("로그인 성공!");
      if (content.email && content.username) {
        this.props.userStateChange(true, true, content.username, content.email);    
        this.props.history.push("/");
      }
    })
    .catch(error => alert(error));
  }

  render() {
    console.log('login render.');
    return (
      <Fragment>
        <LoginForm
            username={this.state.username}
            password={this.state.password}
            changeUsername={e => this.valChangeControl(e)}
            changePassword={e => this.valChangeControl(e)}
            normalLogin={e => this.normalLogin(e)}
            validate={this.validateForm}
        />
      </Fragment>
    );
  }
}
