import React, {Fragment} from 'react';
import { Navbar, Nav, NavItem, MenuItem, NavDropdown, Form, FormControl, Button } from 'react-bootstrap';

import { Link } from "react-router-dom";
import { LinkContainer } from "react-router-bootstrap";

// 화면 위쪽에 나타나는 검은색 바의 내용물을 결정한다.


/*const NavBar = ({isLogin, username, logout}) => {
  return (
      <Navbar inverse>
        <Navbar.Header>
          <Navbar.Brand>
            {isLogin
              ? <Link to="/">{username}</Link>
              : 'Capstone Test' //로그인 화면 왼쪽 위 문구 결정
            }
          </Navbar.Brand>
          <Navbar.Toggle />
        </Navbar.Header>
        <Navbar.Collapse>
          <Nav pullRight>
            {isLogin
              ? <NavItem onClick={logout}>Logout</NavItem>
              : <Fragment>
                  <LinkContainer to="/signup">
                    <NavItem>Signup</NavItem>
                  </LinkContainer>
                  <LinkContainer to="/login">
                    <NavItem>Login</NavItem>
                  </LinkContainer>
                </Fragment>
            }
          </Nav>
        </Navbar.Collapse>
      </Navbar>   
  );
}*/

const NavBar = ({isLogin, username ,logout}) => {
  return(
    <Navbar>
    <Navbar.Header>
      <Navbar.Brand>
        <Link to="/">Capstone Test</Link>
      </Navbar.Brand>
    </Navbar.Header>
    <Nav bsStyle='pills'>

      <NavItem eventKey={1} href="/signup">
        회원가입
      </NavItem>
      {
        isLogin
        ?
        <NavItem eventKey={2} onSelect={logout}>
          로그아웃
        </NavItem>
        :
        <NavItem eventKey={3} href='/login'>
          로그인
        </NavItem>
      }

      {isLogin ?
        <NavDropdown eventKey={4} title="메뉴" id="basic-nav-dropdown">
          <MenuItem eventKey={4.1}>여기에</MenuItem>
          <MenuItem eventKey={4.2}>회원정보나</MenuItem>
          <MenuItem eventKey={4.3}>팀 관리 같은걸</MenuItem>
          <MenuItem divider />
          <MenuItem eventKey={4.4}>넣으면 될것 같아요</MenuItem>
        </NavDropdown>
      : <></>
      }
      </Nav>
    </Navbar>
  );
}
export default NavBar;
