import React from 'react';
import "./GoogleLoginButton.css"
class GoogleLoginButton extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoggedIn: false
    };
  }

  toggleLoggedIn = () => this.setState(state => {
    return { isLoggedIn: !state.isLoggedIn };
  });

  onSignIn = googleUser => {

    this.props.onSignIn(googleUser);

  };

  renderGoogleLoginButton = () => {   
     console.log('rendering google signin button');

     window.gapi.signin2.render('my-signin2', {
      scope: 'profile email',
       width: 200,
       height: 40,
       longtitle: false,
       theme: 'dark',
       onsuccess: this.onSignIn,
       //onsuccess: this.props.onSignIn,
     });
    return (<input type="submit" value="Login" placeholder="login">button</input>);
  };

  logout = () => {
    console.log('logout');

    let auth2 = window.gapi && window.gapi.auth2.getAuthInstance();
    if (auth2) {
      auth2
        .signOut()
        .then(() => {
          this.toggleLoggedIn();
          console.log('Logged out successfully');
        })
        .catch(err => {
          console.log('Error while logging out', err);
        });
    } else {
      console.log('error while logging out');
    }
  };

  componentDidMount() {
    window.addEventListener('google-loaded', this.renderGoogleLoginButton);
    window.gapi && this.renderGoogleLoginButton();
  }

  render() {
    return (
      <div>
        <input type="submit" id="my-signin2" className="fadeIn register form-control"></input>
        {this.state.isLoggedIn && (
          //  <input style={{ width: 200, height: 40, textAlign: 'center'}} onClick={this.logout}>
            <input type="button" id="login-google" className="fadeIn register form-control"> onClick={this.logout}>
            Logout
          </input>
        )}
      </div>
    );
  }
}

export default GoogleLoginButton;
