import React, { Component } from 'react';
import ForgotIDForm from "../components/LoginComponents/ForgotIDForm";

export default class ForgotID extends Component{
    constructor(props){
        super(props);
        console.log('this test.');
        this.state = {
            email : ""
        };
        this.sendID=this.sendID.bind(this);
    }

     //유저 로그인 상태 체크
    componentDidMount() {
    if (this.props.isLogin) {
      this.props.history.push("/");
    }
  }


    sendID(e) {
        e.preventDefault();


        let emailData={
            Idorpassword:'id',
            username:'',
            email: this.state.email
        }

        fetch("http://localhost/api/forgot", {
          method: "POST",
          headers: {
            'Content-Type' : 'application/json',
          },
          credentials: 'include',
          body: JSON.stringify(emailData)
        })
        .then(res=>res.json())
        .then(content => {
            if(content.hasOwnProperty('error'))
                throw Error(content['올바르지 않은 ID입니다']);
            else
              this.props.history.push('/display-id',{username: content.username});

        }).catch(error=>alert(error));
      }

      valChangeControl(e){
        let target_id=e.target.id;
        let target_val=e.target.value;
        console.log("changeControl!");
        this.setState({
          [target_id]: target_val
        });
      }


    render(){
        console.log("forgot id test.");

        return(      
            <div>
                <ForgotIDForm
                email={this.state.email}
                changeEmail={e => this.valChangeControl(e)}
                sendID = {e => this.sendID(e)}
                />
            </div>
        );
    }
}
