import React, { Component, Fragment } from "react";
import LoginForm from "../components/LoginComponents/LoginForm";

//로그인
export default class Login extends Component { //export default : 다른 모듈에서 이 모듈을 import할 때 내보낼 대표 값
  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: ""
    };
    console.log("로그인 시작.");
  }

  //유저 로그인 상태 체크
  componentDidMount() {
    if (this.props.isLogin) {
      this.props.history.push("/");
    }
  }

  validateField(username, password) {
    return (username&&username.length>0&&password&&password.length>0);
  }

  valChangeControl(e){
    let target_id=e.target.id;
    let target_val=e.target.value;
    console.log("changeControl!");
    this.setState({
      [target_id]: target_val
    });
    console.log("CC test.");
    console.log(this.state.username);
    console.log(this.state.password);
  }

  //구글 로그인 구현하기


  // 일반 로그인
  normalLogin(e) {
    e.preventDefault();

    let isMailAuthenticated=true;
    let data={
      username: this.state.username,
      password: this.state.password
    }

    let errorCheck = response => {
       if(response.hasOwnProperty('error')){
         if(response.hasOwnProperty('email')){
            isMailAuthenticated=false;
         }
         else
            throw Error(response['error']);
       }
       return response;
    }

    fetch("http://localhost/api/jwt-login", {
      method: "POST",
      headers: {
        'Content-Type' : 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(data)
    })
    .then(res=>res.json())
    .then(errorCheck)
    .then(content => {
      this.props.userStateChange(true, isMailAuthenticated, content.username, content.email);
    }).catch(error=>alert(error));
  }

  render() {
    console.log('login render.');
    return (
      <Fragment>
        <LoginForm
            username={this.state.username}
            password={this.state.password}
            changeUsername={e => this.valChangeControl(e)}
            changePassword={e => this.valChangeControl(e)}
            normalLogin={e => this.normalLogin(e)}
            validate={this.validateForm}
        />
      </Fragment>
    );
  }
}
