import React, { Component } from 'react';
import MailAuthForm from "../components/auth/MailAuthForm"

export default class MailAuth extends Component{
    constructor(props){
        super(props);

        this.state = {
            email : ""
        }
    }

    async resendAuthEmail(){
        let data={
            email: this.state.email
        };

        let handleErrors = response =>{
            if(response.message){
                throw Error(response.message);
            }
            return response;
        }

        fetch("http://localhost/api/send-auth-email", {
            method: "POST",
            body: JSON.stringify(data)
        })
        .then(handleErrors)
        .then(res=>res.json())
        .then(json=>{
            console.log(json);
            console.log("메일 재발송 완료.");
        });
    }

    render(){
        return(
            <MailAuthForm
                email={this.state.email}
                resendAuthEmail={this.resendAuthEmail}
            />
        );
    }
}
