import React, { Component } from "react";
import SignupForm from "../components/auth/SignupForm";

// 회원가입할 때 사용하는 컴포넌트
export default class Signup extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      password_val: "",
      email: "",
      phone: "",
    };
  }

  componentDidMount() {
    if (this.props.isAuthenticated) {
      this.props.history.push("/");
    }
  }

  validateForm(username, password, password_val) { //여기서 회원가입 필드들의 유효성 확인, 
    console.log(state.password_val);
    return (username && username.length > 0) && (password && password.length > 0) && (password==password_val);
  }

  handleChange = event => {
    this.setState({
      [event.target.id]: event.target.value
    });
  }

  async handleSubmit(submitEvent) {
    let data = {
      username: this.state.username,
      password: this.state.password,
      email: this.state.email,
      phone_num: this.state.phone,
      social_auth: "",
      is_mail_authenticated: false,
    };

    console.log("data : " + JSON.stringify(data))
    submitEvent.preventDefault();

    let handleErrors = response => {
      if (!response.ok) {
        throw Error(response.statusText);
      }
      return response;
    }

    fetch('http://localhost:8000/api/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(data)
    })
    .then(handleErrors)
    .then(res => res.json())
    .then(json => {
      if (json.username && json.token) {
        console.log("회원가입 완료!");
        this.props.userHasAuthenticated(true, json.username, json.token);
        this.props.history.push("/");
      }
    })
    .catch(error => alert(error));
  }

  render() {
    return (
      <SignupForm
        username={this.state.username}
        password={this.state.password}
        handleChangeUsername={e => this.handleChange(e)}
        handleChangePassword={e => this.handleChange(e)}
        handleChangePassword_val={e => this.handleChange(e)}
        handleChangeEmail={e => this.handleChange(e)}
        handleChangePhone={e => this.handleChange(e)}
        handleSubmit={e => this.handleSubmit(e)}
        validate={this.validateForm}
      />
    );
  }
}
