import React, { Component, Fragment } from "react";
import NavBar from "./AuthRoutingComponents/Nav";
import Route from "./Route";
import { withRouter } from "react-router-dom"; //로그아웃 했을 때 로그인 화면으로 리다이렉션하기 위해 import

class App extends Component {
  constructor(props) {
    super(props); //App 컴포넌트가 상속받은 Component가 지니고 있던 생성자를 super를 통해 미리 실행한다.
    console.log('App prop test.');
    this.state = { //컴포넌트의 state 정의
      username: "",
      email: "",
      isCheck: false, //사용자가 로그인 상태인지 체크하면 true로 변경
      isLogin: false,
      isMailAuthenticated: false
    };
    console.log(this.state);
  }

  // user 정보 받아오기
  componentDidMount() { //컴포넌트가 만들어지고 render가 호출된 이후에 호출되는 메소드
    let errorCheck = response => {
      console.log("err cherk.");
      console.log(response);

      if(!this.state.isCheck){
        this.setState({
          isCheck: true
        });
      }
      if(response.ok&&!this.state.isLogin){
        this.setState({
          isLogin: true 
        });
      }
      return response;
    } 

    fetch('http://localhost/api/user', { // JWT 토큰이 저장되어 있는지, 그리고 저장되어 있다면 해당 JWT 토큰이 유효한지 확인
      method: "GET",
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
    })
    .then(errorCheck)
    .then(res=>res.json())
    .then(content=>{
      if(this.state.isLogin){ // 사용자가 로그인 중일 때
         fetch('http://localhost/api/jwt-refresh', { //JWT 토큰 재발급
            method: "POST", 
            headers: {
              'Content-Type' : 'application/json',
            },
            credentials: 'include',
         })
         .then(errorCheck)
         .then(res=>res.json())
         .then(content=>{
            console.log("토큰이 재발급되었습니다.");
            console.log(content);
         }).catch(error=>console.log('JWT 토큰 재발급 에러!'));
      }
    }).catch(error=>console.log('로그인 체크 에러!'));

  }

  userStateChange = (authenticated, mailAuthenticated, username, email) => {
    console.log("thisStateTest.", this.state);
    if((!this.state.isLogin||this.state.isLogin==false) && authenticated==true) localStorage.setItem('isLogin', true);
    if((!this.state.isMailAuthenticated||this.state.isMailAuthenticated==false) && mailAuthenticated==true) localStorage.setItem('isMailAuthenticated', true);
    if(email=='google'||email=='facebook'){ //소셜 로그인
      this.setState({
        isLogin: authenticated,
        username: username,
        isMailAuthenticated: true
      });
    }
    else{
      this.setState({
        isLogin: authenticated,
        isMailAuthenticated: mailAuthenticated,
        username: username,
        email: email
      });
    }
  }

  deleteJWTToken(){
    let isTokenStored=true;
    let tokenCheck = response => {
      if(!response.ok){
        isTokenStored=false;
      }
      return response;
    }

    try {
      fetch('http://localhost/api/logout', {
        method: "POST",
        headers: {
          'Content-Type': 'application/json'
        },
        credentials: 'include',
      })
      .then(tokenCheck)
      .then(res=>{
        if(isTokenStored){
          console.log(res);
          this.setState({
            isLogin: false,
            isMailAuthenticated: false,
            username: '',
            userid: ''
          });
          console.log('Logged out successfully');
          this.props.history.push("/login");
        }
      });

    }catch{
      console.log("error!");
      this.setState({
        isLogin: false,
        userid: ''
      });
      console.log('Logged out successfully');
    }
  }

  // 로그아웃시 서버로 요청 보내서 JWT 토큰이 저장된 httponly 쿠키 제거
  logout = () => {
    localStorage.removeItem('isLogin');
    localStorage.removeItem('isMailAuthenticated');
    this.deleteJWTToken();
  }

  render() {
    const childProps = {
      username: this.state.username,
      useremail: this.state.email,
      isLogin: this.state.isLogin,
      isMailAuthenticated:this.state.isMailAuthenticated,
      userStateChange: this.userStateChange
    };
    console.log("child test.");
    console.log(childProps);
    return (   
       <Fragment>
          { this.state && this.state.isCheck &&
          <NavBar        
            isLogin={this.state.isLogin}
            username={this.state.username}
            logout={this.logout}
          />
          }
          { this.state && this.state.isCheck &&
            <Route childProps={childProps}/>
          }
      </Fragment>
    );
  }
}

//export default App;
export default withRouter(App); //로그아웃 했을 때 로그인화면으로 리다이렉트하기 위해 바꿔준다.
